import{_ as t,c as i,a as r,o as l}from"./app-DPjPDnzl.js";const n={};function a(o,e){return l(),i("div",null,e[0]||(e[0]=[r('<h2 id="你知道移动端如何适配吗" tabindex="-1"><a class="header-anchor" href="#你知道移动端如何适配吗"><span>你知道移动端如何适配吗</span></a></h2><p>达到两种效果：</p><ul><li>自适应 屏幕大小变化，来自动调节尺寸大小</li><li>响应式 会随着屏幕变动而自动调整</li></ul><p>方案：</p><ol><li><p>百分比 应用较少</p></li><li><p>rem单位+动态html的font-size rem是相对于根元素html的font-size来设置的，通过在不同屏幕尺寸下、动态的修改html元素的font-size以此来达到适配效果</p><p>可以使用第三方的插件来实现</p><p>媒体查询 需要针对不同的屏幕编写大量的媒体查询 如果动态改变尺寸，不会实时更新，只是一个个区间</p></li><li><p>viewport 通常viewport是指视窗、视口，即浏览器用来显示网页的那部分区域。在移动端开发中，我们希望页面宽度和设备宽度一致，并把这个viewport称为ideal viewport（理想视口）。我们设置public/index.html添加viewport元数据标签，就是为了得到一个ideal viewport。</p></li></ol><p><a href="https://juejin.cn/post/7335245199109652516" target="_blank" rel="noopener noreferrer">推荐文章1</a><a href="https://juejin.cn/post/7277875605538226195?searchId=2024022819314355E62BEC97EFBF8B563B#heading-4" target="_blank" rel="noopener noreferrer">推荐文章2</a></p>',6)]))}const s=t(n,[["render",a],["__file","index.html.vue"]]),c=JSON.parse('{"path":"/interview/%E8%A7%A3%E5%86%B3%E6%96%B9%E6%A1%88/%E7%A7%BB%E5%8A%A8%E7%AB%AF%E9%80%82%E9%85%8D/","title":"","lang":"zh-CN","frontmatter":{},"headers":[{"level":2,"title":"你知道移动端如何适配吗","slug":"你知道移动端如何适配吗","link":"#你知道移动端如何适配吗","children":[]}],"git":{"updatedTime":1738722167000,"contributors":[{"name":"LiHegui","username":"LiHegui","email":"1487647822@qq.com","commits":1,"url":"https://github.com/LiHegui"}]},"filePathRelative":"interview/解决方案/移动端适配/index.md"}');export{s as comp,c as data};
